part1 = print $ foldl f 0 input
  where f acc ')' = acc - 1
        f acc '(' = acc + 1

-- Part 2

part2 = print $ go input' 0
  where input' = zip input [1..]

        go :: [(Char, Int)] -> Int -> Int
        go ((')', x):xs) 0   = x
        go ((')', x):xs) pos = go xs (pos - 1)
        go (('(', x):xs) pos = go xs (pos + 1)